function units(score){var ct;if((score>=85)&&(score<=100)){ct=5.00}
else if(score>=80&&score<=84){ct=4.50}
else if(score>=75&&score<=79){ct=4.00}
else if(score>=70&&score<=74){ct=3.50}
else if(score>=65&&score<=69){ct=3.00}
else if(score>=60&&score<=64){ct=2.50}
else if(score>=55&&score<=59){ct=2.00}
else if(score>=50&&score<=54){ct=1.50}
else if(score>=50){ct=0.00}
else{ct=0.00}
return ct}
function grades(status){if((status<1.5)){return "Fail"}
else if((status>=1.5)&&(status<1.99)){return "Pass"}
else if((status>=2.0)&&(status<2.99)){return "Second Class Lower"}
else if((status>=3.0)&&(status<3.99)){return "Second Class Upper"}
else if((status>=4.00)&&(status<=5.0)){return "Firts Class"}}